const initState = {
    empID: null,
    adminID: null,
    eLogin : false,
    aLogin : false
}
const initAction = {
    empId : 565
}
const rootReducer = (state=initState, action)=>{
    console.log(state)
    if (action.type == 'updateEmpID')
    {
    console.log('updating state')
        return {
            empID : parseInt( action.empid) ,
            eLogin : action.eLogin,
            aLogin : false,
            adminID : null
        }
    }
    return {
        ...state,
        empID : initAction.empId
    };
}
export default rootReducer;
