        products : data
      })
    } 
    fetch("/productList", {
      method: 'POST',
      headers: {
      'Content-Type': 'application/json'},
    }).then(function(response) {
      if (response.status >= 400) {
        throw new Error("Bad response from server");
      }
      return response.json();
    }).then(function(data) {
      console.log(data);
      //tempstate.products=data
      if (tempstate.products.length === 0)
      setP(data) 
    }).catch(function(err) {
      console.log(err)
    });
