/*!

=========================================================
* Argon Dashboard React - v1.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";

// reactstrap components
import {
  Badge,
  Card,
  Col,
  FormGroup,
  Input,
  CardHeader,
  CardFooter,
  Button,
  Media,
  Progress,
  Table,
  Container,
  Row,
  Form
} from "reactstrap";
// core components
import Header from "components/Headers/Header.jsx";

class Tables extends React.Component {
  constructor(props) {
    super(props)  
    this.state={
      products:[]
    }
    this.handleSubmit = this.handleSubmit.bind(this)

  }
  handleSubmit(event){
    event.preventDefault();

    const data = {
      pid : event.target.pid.value,
      cost : event.target.cost.value,
      stock : event.target.stock.value
    }
    const setP = (data) =>{
      this.setState({
        products : data
      })
    }
    console.log(event.target.id)

    if(event.target.id == "addProduct")
    {
      fetch("/addPro", {
        method: 'POST',
        headers: {
        'Content-Type': 'application/json'},
        body: JSON.stringify(data)
        })
        .then(function(response) {
          if (response.status >= 400) {
            throw new Error("Bad response from server");
          }
          return response.json();
        })
        .then(function(data){         
        if(data.status === "success"){
          document.getElementById('addProduct').reset();
          setP(data.data)
        }
        }).catch(function(err) {
        console.log(err)
        });
    }
    else{
      fetch("/updatePro", {
        method: 'POST',
        headers: {
        'Content-Type': 'application/json'},
        body: JSON.stringify(data)
        })
        .then(function(response) {
          if (response.status >= 400) {
            throw new Error("Bad response from server");
          }
          return response.json();
        })
        .then(function(data){         
        if(data.status === "success"){
          document.getElementById('updateForm').reset();
          setP(data.data)
        }
        }).catch(function(err) {
        console.log(err)
        });
    }


  }


  componentDidMount(){
    const setP = (data) =>{
      this.setState ({
        products : data
      })}

      fetch("/productList", {
        method: 'POST',
        headers: {
        'Content-Type': 'application/json'},
      }).then(function(response) {
        if (response.status >= 400) {
          throw new Error("Bad response from server");
        }
        return response.json();
      }).then(function(data) {
        setP(data)
        console.log(data)
      }).catch(function(err) {
        console.log(err)
      });

    }


  disp=(pro)=>{ 
    return pro.map((data,key)=>{
      data.quantity = 0;
      return (
        <tr key = {key}>
                      <th scope="row">

                          <Media>
                            <span className="mb-0 text-sm">
                              {data.name}
                            </span>
                          </Media>

                      </th>
                      <td>
                        <Badge color="" className="badge-dot mr-4">
                          <i className="bg-warning" />
                          {data.pid}
                        </Badge>
                      </td>
                      <td>{data.cost} ₹​ </td>

                      <td>
                        <div className="d-flex align-items-center">
                          <span className="mr-2">{data.stock}</span>
                          <div>
                            <Progress
                              max="1000"
                              value={data.stock}
                              barClassName="bg-danger"
                            />
                          </div>
                        </div>
                      </td>


                    </tr>
      )

    })
  }

  render() {
    return (
      <>
        <Header />
        {/* Page content */}
        <Container className="mt--7" fluid>
          {/* Table */}
          <Row>
            <div className="col">
              <Card className=" bg-default shadow">
                <CardHeader className="bg-transparent border-0">
                  <h3 className="mb-0 text-white">Product tables</h3>
                </CardHeader>
                <Table 
                  className="align-items-center table-dark table-flush"
                  responsive>
                  <thead className="thead-dark">
                    <tr>
                      <th scope="col">Product</th>
                      <th scope="col">Product ID</th>
                      <th scope="col">Cost</th>
                      <th scope="col">Stock</th>
                    </tr>
                  </thead>
                  <tbody>{this.disp(this.state.products)}</tbody>
                </Table>
                <CardFooter className="py-4 bg-default shadow">

                  <nav aria-label="...">

                  <Form onSubmit={this.handleSubmit} method="post" id = "updateForm">
                  <Row className="justify-content-center">

                        <Col lg="3">
                          <FormGroup>
                            <label
                              className="form-control-label"
                            >
                            <b className="text-white">PID</b>
                            </label>
                            <Input
                              className="form-control-alternative text-dark"
                              id="pid"
                              type="number"
                              name="pid"
                              defaultValue = "0"
                            />
                          </FormGroup>
                        </Col>

                        <Col lg="3">
                          <FormGroup>
                            <label
                              className="form-control-label"
                            >
                            <b className="text-white">COST</b>
