*/
import React from "react";
import {connect} from 'react-redux';

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Col
} from "reactstrap";
class AdminLogin extends React.Component {
  constructor(props) {
    super(props);
    console.log(props)
  } 


    handleSubmit(event){
      event.preventDefault();
      console.log('Sign In')
      console.log("ELogin: "+this.props.eLogin+" ALogin: "+this.props.aLogin)
      if (!(this.props.eLogin ^ this.props.aLogin))
      {
      const tempProp = this.props;
      console.log(event.target.adminid.value);
      const data = {
        adminid :  event.target.adminid.value, 
        password : event.target.password.value, 
        mssg : ''
      }

      fetch("/adlogin", {
        method: 'POST',
        headers: {
        'Content-Type': 'application/json'},
        body: JSON.stringify(data) 
    }).then(function(response) {
        if (response.status >= 400) {
          throw new Error("Bad response from server");
        }
        return response.json();
    }).then(function(dat) {

        if(dat.success)
        {
        tempProp.updateEmpId(data.adminid)
        window.location.assign("/admin/Notification");
        }
        else
         alert('Wrong Password')
       // redirect to other page
    }).catch(function(err) {
        console.log(err)
    });
    }
    else alert('eMPLOYEE Should Logout');

    }

  render() {

  }
}
const mapStateToProps = (state) =>{
  return {
    eLogin : state.eLogin,
    aLogin : state.aLogin
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    updateEmpId : (id) =>{
      dispatch({
        type : 'updateAdminID',
        adminid : id,
        aLogin : true
      })
    }
  }
}
export default connect(mapStateToProps,mapDispatchToProps)(AdminLogin);
