/*!

=========================================================
* Argon Dashboard React - v1.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import {connect} from "react-redux";
// reactstrap components
import firebase from '../../config/fbConfig'
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Container,
  Row,
  Col
} from "reactstrap";
// core components
import UserHeader from "components/Headers/UserHeader.jsx";

class Notification extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      adminName: null,
      employee : null
    }
    this.handleSubmit = this.handleSubmit.bind(this)
  }
  renderCard =(doc)=>{
      const cardList = document.getElementById('cardList');
      let li = document.createElement('li');
      let name = document.createElement('span');
      let city = document.createElement('span');

      li.setAttribute('data-id', doc.id);
      name.textContent = doc.name;
      city.textContent = doc.phoneno;

      li.appendChild(name);
      li.appendChild(city);
      cardList.appendChild(li);
  }
  componentDidMount(){
    console.log(this.props)
    const setP=(name)=>{
      this.setState ({
        ...this.state,
        adminName : name
      })

      const db = firebase.firestore();

    db.collection("argon")
    .onSnapshot((snap) => {
      const changes = snap.docChanges();
      changes.forEach((change) => {
        console.log(change)
          this.renderCard(change)
      })
    })
    }
    const db = firebase.firestore();

    db.collection("argon")
    .onSnapshot((snap) => {
      const changes = snap.docChanges();
      changes.forEach((change) =>{console.log(change.doc.data())
      this.renderCard(change.doc.data())
      })
    })

    const data = {adminId :this.props.adminId}

    fetch("/adminName", {
      method: 'POST',
      headers: {
      'Content-Type': 'application/json'},
      body: JSON.stringify(data) 
      })
      .then(function(response) {
        if (response.status >= 400) {
          throw new Error("Bad response from server");
        }
        return response.json();
      })
      .then(function(data){         
      if(data.status === "success"){
        setP(data.name[0].name)
      }
      }).catch(function(err) {
      console.log(err)
      });
  }

    handleSubmit(event){

      event.preventDefault();
      const setE = (emp)=>{
        this.setState({
          ...this.state,
          employee : emp
        })
      }
      const data = {
        name : event.target.name.value
        ,password : event.target.password.value
        ,phoneno : event.target.phoneno.value
        ,address :event.target.address.value
        ,accno: event.target.accno.value
      }


      fetch("/empreg", {
        method: 'POST',
        headers: {
        'Content-Type': 'application/json'},
        body: JSON.stringify(data) 
        })
        .then(function(response) {
          if (response.status >= 400) {
            throw new Error("Bad response from server");
          }
          return response.json();
        })
        .then(function(data){         
        if(data.status === "success"){
          setE(data.data[0])
          document.getElementById("regForm").reset()
        }
        }).catch(function(err) {
        console.log(err)
        });
    }
  render() {
    return (
      <>
        <UserHeader adminName={this.state.adminName}/>
        {/* Page content */}
        <ul id="cardList">

        </ul>
      </>
    );

  }
}

export default (Notification);
