/*!

=========================================================
* Argon Dashboard React - v1.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import {connect} from "react-redux";
// reactstrap components
import firebase from '../../config/fbConfig'
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Container,
  Row,
  Col
} from "reactstrap";
// core components
import UserHeader from "components/Headers/UserHeader.jsx";

class Notification extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      adminName: null,
      employee : null
    }
    this.handleSubmit = this.handleSubmit.bind(this)
  }
  renderCard =(doc)=>{
      const cardList = document.getElementById('cardList');
      let li = document.createElement('li');
      let name = document.createElement('span');
      let city = document.createElement('span');
      let li1 = (
      <Col lg="6" xl="3">
                  <Card className="card-stats mb-4 mb-xl-0">
                    <CardBody>
                      <Row>
                        <div className="col">
                          <CardTitle
                            tag="h5"
                            className="text-uppercase text-muted mb-0"
                          >
                            New users
                          </CardTitle>
                          <span className="h2 font-weight-bold mb-0">
                            2,356
                          </span>
                        </div>
                        <Col className="col-auto">
                          <div className="icon icon-shape bg-warning text-white rounded-circle shadow">
                            <i className="fas fa-chart-pie" />
                          </div>
                        </Col>
                      </Row>
                      <p className="mt-3 mb-0 text-muted text-sm">
                        <span className="text-danger mr-2">
                          <i className="fas fa-arrow-down" /> doc.phoneno
                        </span>{" "}
                        <span className="text-nowrap">doc.accno</span>
                      </p>
                    </CardBody>
                  </Card>
                </Col>)

      li.setAttribute('data-id', doc.id);
      name.textContent = doc.name;
      city.textContent = doc.phoneno;

      li.appendChild(name);
      li.appendChild(city);
      cardList.appendChild(li);
  }
  componentDidMount(){
    console.log(this.props)
    const setP=(name)=>{
      this.setState ({
        ...this.state,
        adminName : name
      })

      const db = firebase.firestore();

    db.collection("argon")
    .onSnapshot((snap) => {
      const changes = snap.docChanges();
      changes.forEach((change) => {
        console.log(change)
          this.renderCard(change)
      })
    })
    }
    const db = firebase.firestore();

    db.collection("argon")
    .onSnapshot((snap) => {
      const changes = snap.docChanges();
      changes.forEach((change) =>{console.log(change.doc.data())
      this.renderCard(change.doc.data())
      })
    })


  }

    handleSubmit(event){

      event.preventDefault();
      const setE = (emp)=>{
        this.setState({
          ...this.state,
          employee : emp
        })
      }
      const data = {
        name : event.target.name.value
        ,password : event.target.password.value
        ,phoneno : event.target.phoneno.value
        ,address :event.target.address.value
        ,accno: event.target.accno.value
      }


      fetch("/empreg", {
        method: 'POST',
        headers: {
        'Content-Type': 'application/json'},
        body: JSON.stringify(data) 
        })
        .then(function(response) {
          if (response.status >= 400) {
            throw new Error("Bad response from server");
          }
          return response.json();
        })
        .then(function(data){         
        if(data.status === "success"){
          setE(data.data[0])
          document.getElementById("regForm").reset()
        }
        }).catch(function(err) {
        console.log(err)
        });
    }
  render() {
    return (
      <>
        <UserHeader adminName={this.state.adminName}/>
        {/* Page content */}
        <ul id="cardList">

        </ul>
      </>
    );

  }
}

export default (Notification);
